// src/components/Categories.jsx
import React, { useState, useEffect } from "react";
import {
  Typography,
  Box,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CircularProgress,
  useMediaQuery,
  keyframes,
} from "@mui/material";
import { styled, useTheme } from "@mui/system";
import { useTranslation } from "react-i18next";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// Animation keyframes
const slideIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

// Custom styled components
const CategoriesContainer = styled(Box)(({ theme }) => ({
  maxWidth: 1400,
  margin: "auto",
  padding: theme.spacing(2),
  backgroundColor: "#f7f7f7",
  borderRadius: "12px",
  minHeight: "80vh",
  animation: `${slideIn} 0.5s ease-out`,
  [theme.breakpoints.down("sm")]: {
    padding: theme.spacing(1),
  },
}));

const CategoryCard = styled(Card)(({ theme }) => ({
  borderRadius: "12px",
  boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
  backgroundColor: "#fff",
  transition: "transform 0.3s, box-shadow 0.3s",
  "&:hover": {
    transform: "translateY(-4px)",
    boxShadow: "0 8px 24px rgba(0, 0, 0, 0.15)",
  },
  cursor: "pointer",
}));

const CardTitle = styled(Typography)(({ theme }) => ({
  color: "#111",
  fontWeight: 600,
  textAlign: "center",
  fontSize: { xs: "0.9rem", sm: "1rem", md: "1.25rem" },
}));

function Categories() {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        "https://eshop-backend-e11f.onrender.com/api/products"
      );
      const uniqueCategories = [
        ...new Set(response.data.map((product) => product.category)),
      ];
      setCategories(uniqueCategories);
      setError("");
    } catch (error) {
      setError(t("Failed to load categories"));
      console.error(
        "Fetch categories error:",
        error.response?.data || error.message
      );
    } finally {
      setLoading(false);
    }
  };

  const handleCategoryClick = (category) => {
    navigate(`/products?category=${encodeURIComponent(category)}`);
  };

  const handleImageError = (e) => {
    e.target.src = "https://picsum.photos/200?blur=2"; // Fallback image
  };

  const categoryImages = {
    All: "https://picsum.photos/200?random=1",
    "T-Shirts":
      "data:image/webp;base64,",
    Jackets:
      "data:image/jpeg;base64,",
    Pants:
      "https://cdn.shopify.com/s/files/1/2987/0758/files/Como_Suit_Pants-Pants-LDM501001-0101-Black-5_70d8dfd7-616d-4518-8002-3216873d3935.jpg?v=1702994372",
    Accessories:
      "data:image/jpeg;base64,",
  };

  return (
    <CategoriesContainer>
      <Typography
        variant={isMobile ? "h5" : "h3"}
        sx={{
          color: "#111",
          fontWeight: 700,
          textAlign: "center",
          mb: isMobile ? 2 : 4,
        }}
      >
        {t("Shop by Category")}
      </Typography>
      {loading ? (
        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            py: isMobile ? 2 : 4,
          }}
        >
          <CircularProgress
            sx={{ color: "#f0c14b" }}
            size={isMobile ? 30 : 40}
          />
        </Box>
      ) : error ? (
        <Typography
          color="error"
          sx={{
            mb: 2,
            textAlign: "center",
            fontSize: { xs: 12, sm: 16 },
            bgcolor: "#ffebee",
            p: 1,
            borderRadius: 2,
          }}
        >
          {error}
        </Typography>
      ) : (
        <Grid container spacing={isMobile ? 1 : 3}>
          {categories.map((category) => (
            <Grid item xs={12} sm={6} md={4} key={category}>
              <CategoryCard onClick={() => handleCategoryClick(category)}>
                <CardMedia
                  component="img"
                  height={isMobile ? "150" : "200"}
                  image={
                    categoryImages[category] ||
                    "https://picsum.photos/200?blur=2"
                  }
                  alt={category}
                  onError={handleImageError}
                  sx={{ borderRadius: "12px 12px 0 0", objectFit: "cover" }}
                />
                <CardContent sx={{ py: isMobile ? 1 : 2 }}>
                  <CardTitle>{t(category)}</CardTitle>
                </CardContent>
              </CategoryCard>
            </Grid>
          ))}
        </Grid>
      )}
    </CategoriesContainer>
  );
}

export default Categories;
